import React from 'react';
import api from '../utils/Api';

function Main(props) {
    const [userAvatar, setUserAvatar] = React.useState('');
    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        Promise.all([api.getUserInfo(), api.getInitialCards()])
            .then(([userData, cards]) => {
                setUserName(userData.name);
                setUserDescription(userData.about);
                setUserAvatar(userData.avatar);
                setCards(cards);
            }).catch((e) => console.log(e));
    },[]);

return(
    <main className="content">
    <section className="profile">
        <div className="profile__avatar-button">
            <img className="profile__avatar" src={userAvatar} alt="Жак-Ив Кусто"/>
        </div>    
        <div className="profile__info">
            <h1 className="profile__title">Жак-Ив Кусто</h1>
            <button type="button" className="profile__button-edit"></button>
            <p className="profile__subtitle">Исследователь океана</p>
        </div>
        <button type="button" className="profile__add-button"></button>
    </section>
    <section className="elements">
        <template id="elements-card">
            <article className="element">
                <button type="button" className="element__remove"></button>
                <img className="element__image" alt="Фото Карточки"/> 
                <div className="element__group">
                    <h2 className="element__name"></h2>
                    <div className="element__like-field">
                        <button type="button" className="element__like"></button>  
                        <p className="element__like-counter"></p>
                    </div>    
                </div>
            </article>
        </template>
    </section>
    </main> 
)
}

export default Main;
