import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Card({card, }) {
    const currentUser = React.useContext(CurrentUserContext);
    const isOwn = card.owner._id === currentUser._id;
    const cardDeleteButtonClassName = (
        `card__delete-button ${isOwn ? 'card__delete-button_visible' : 'card__delete-button_hidden'}`
      ); 
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    const cardLikeButtonClassName = `card__like-button ${isLiked ? 'card__like-button_visible' : 'card__like-button_hidden'}`;  

    function handleClick() {
        props.onCardClick(props.card);
      }  
    
    return( 
        <article className="element">
            <button type="button" className={cardDeleteButtonClassName}></button>
            <img className="element__image" alt={props.card.name} src={props.card.link} onClick={handleClick} /> 
            <div className="element__group">
                <h2 className="element__name">{props.card.name}</h2>
                <div className="element__like-field">
                    <button type="button" className={cardLikeButtonClassName}></button>  
                    <p className="element__like-counter">{props.card.likes.length}</p>
                </div>    
            </div>
        </article>
    )
}
export default Card
