import React from "react";
import { useAppDispatch, useAppSelector } from "../store/hooks";
import { setPopupType } from "../store/popupReducer";
import { IPopupWithForm } from "../store/types";
import { getConfig } from "./Config";
import { useEditUserMutation } from "../store/api/actionsApi";

type IPopup = {
  title: string;
  inputs: Array<{ value: string; type: string; id: string }>;
  onSubmit: () => null;
};

const PopupWithForm: React.FC<IPopup> = () => {
  // const popup = useAppSelector((state) => state.popup.closeAllPopups);
  const type = useAppSelector((state) => state.popup.type);
  const [popup, setPopup] = React.useState<IPopup | null>(null);
  const userId = useAppSelector((state) => state.auth.userId);

  const [editUser] = useEditUserMutation();

  React.useEffect(() => {
    const modal = getConfig().find((item) => item.type === type);
    setPopup(modal || null);
  }, [type]);

  const dispatch = useAppDispatch();

  // const handleEditUser = async (e: React.FormEvent) => {
  //   e.preventDefault();
  //   if (userId)
  //     await editUser({
  //       name: name,
  //       about: description,
  //     });
  //   setName("");
  //   setDescription("");
  // };

  if (popup) {
    return (
      <div className={"popup popup_visible"}>
        <div className="popup__container">
          <button
            className="popup__close-button"
            type="button"
            onClick={() => dispatch(setPopupType(null))}
          >
            &times;
          </button>
          <h2 className="popup__title">{popup.title}</h2>
          <form
            className="popup__form"
            // name={props.name}
            onSubmit={onSubmit}
          >
            {popup.inputs.map((input) => {
              return (
                <>
                  <input
                    key={input.id}
                    required
                    value={input.value}
                    onChange={(e) => {
                      // @ts-ignore
                      setPopup((prevState) => {
                        let inputs;
                        if (prevState != null) {
                          inputs = prevState?.inputs.map((i) => {
                            if (i.id === input.id) {
                              i.value = e.target.value;
                            }
                            return i;
                          });
                        }
                        // @ts-ignore
                        return { ...prevState, inputs };
                      });
                    }}
                    className="popup__input popup__input_type_name"
                    name="name"
                    id="name-input"
                    type={input.type}
                    minLength={2}
                    maxLength={40}
                    placeholder={input.value}
                  />
                  <span className="popup__form-error name-input-error"></span>
                </>
              );
            })}
            <button className="popup__save-button" type="submit">
              Сохранить
            </button>
          </form>
        </div>
      </div>
    );
  } else {
    return null;
  }
};
export default PopupWithForm;
